import React, {Component} from 'react';
import './App.css';
import Login from './components/Login'
import Programma from './components/Programma'

class App extends Component {

state = {
  'name': '',
  'token':'',
  'password':''
}

setName = (e)=>{
  this.setState({name:e.target.value})
}

setPassword = (e)=>{
  this.setState({password:e.target.value})
}

setToken = (e) =>{
  e.preventDefault();
  fetch('https://api.ellipsis-earth.com/v2/account/login',{method:'POST',  headers: {'Content-Type': 'application/json'}, body:JSON.stringify({'username':'bruiloft', 'password':this.state.password})})
  .then(res => {
    if(res.status === 400){
      alert("Verkeerd wachtwoord, in de verstuurde mail staat het juiste wachtwoord. Letop dat je geen spaties in je wachtwoord hebt. Houd ook rekening met hoofdletters.")
    }
    return(res.json())
  })
  .then((data) => {
    this.setState({token: 'Bearer ' + data.token})
    console.log(this.state)
  })
  .catch(console.log)}

  render(){

    let programma;
    if(false){
      programma = <Programma/>
    }
  return (
    <div className="App">
    <Login name={this.state.name} setName={this.setName} token = {this.state.token} setToken = {this.setToken} password = {this.state.password} setPassword = {this.setPassword} />
    {programma}
    </div>
  );
}
}
export default App;
