import React, {Component} from 'react';
import './App.css';
import Login from './components/Login'

class App extends Component {

state = {
  'name': '',
  'token':'',
  'password':''
}

setName = (e)=>{
  this.setState({name:e.target.value})
}

setPassword = (e)=>{
  this.setState({password:e.target.value})
}

setToken = (e) =>{
  e.preventDefault();
  fetch('https://api.ellipsis-earth.com/v2/account/login',{method:'POST', body:{'username':'admin', 'password':'birdies'}})
  .then(res => res.json())
  .then((data) => {
    console.log(data)
  })
  .catch(console.log)}

  render(){
  return (
    <div className="App">
    <Login name={this.state.name} setName={this.setName} token = {this.state.token} setToken = {this.setToken} password = {this.state.password} setPassword = {this.setPassword} />
    </div>
  );
}
}
export default App;
