import React, {Component} from 'react';


class Login extends Component {

state = { 'password':''}

setPassword = (e)=>{
  this.setState({password:e.target.value})
}

render(){  return (
  <div>
  <h1> Welkom op de bruiloft van Roos en Eefke </h1>
  <form onSubmint = {this.setToken}>
  <input type = "text" name = "naam" placeholder = "Voer je naam in, deze wordt gebruikt om aan andere gasten te tonen" value = {this.props.name} onChange = {this.props.setName}/>
  <input type = "text" name = "wachtwoord" placeholder = "Voer het wachtwoord uit de email in." value = {this.state.password} onChange = {this.setPassword}/>
   <input type="submit" value= "Ga naar de tuin!" />
   </form>
  </div>
  );
}
}
export default Login;
