}

sent = (e)=>{
  console.log(this.state)
  fetch('https://api.ellipsis-earth.com/v2/geomessage/add',{method:'POST',  headers: {'Content-Type': 'application/json', 'Authorization': this.props.token}, body:JSON.stringify({'mapId':this.props.mapId, 'type': 'polygon', 'limit':30, 'filters':{ 'polygonIds':[16]}})})
}


render(){  return (
  <div>
  <h3> Stuur bericht </h3>
  <input type = "text" name = "wachtwoord" placeholder = "Type een berichtje." value = {this.props.password} onChange = {this.setName}/>
   <input type="button" value= "stuur!" onClick = {this.sent} />
  </div>

  );
}
}
