import React, {Component} from 'react';
import {
  Map,
  TileLayer,
  Pane,
  Marker,
  Popup,
  Circle,
  GeoJSON
} from 'react-leaflet';
import L from 'leaflet'
import 'leaflet/dist/leaflet.css';

class Tuin extends Component {

leafletMap = null
constructor(props) {
  super(props);

  this.leafletMap = React.createRef();

}

componentDidMount() {
  let leafletElement = this.leafletMap.current.leafletElement;

  leafletElement.attributionControl.setPrefix(false);

}


render(){
  let mapCenter = [52.09, 5.10]; // Utrecht
  var mymap = L.map('mapid').setView([51.505, -0.09], 13);

  return (
  <div id="leafletmap">
  <h1> Tuin </h1>
  <p> wmts met tuin en markers </p>
  <Map
    ref={this.leafletMap}
    center={mapCenter}
    zoom={7}
    zoomSnap={0.5}
    zoomDelta={0.5}
    zoomControl={true}
    onViewportChanged={this.onLeafletViewportChanged}
  >
  <Pane style={{ zIndex: 100 }}>
    <TileLayer
      key='base-layer'
      url='https://www.google.com/maps/vt?lyrs=y@189&x={x}&y={y}&z={z}'
      maxNativeZoom={21}
      maxZoom={40}
      noWrap
    />
    </Pane>
  </Map>
  </div>

  );
}
}
export default Tuin;
