import React, {Component} from 'react';
import './App.css';
import Login from './components/Login'
import Programma from './components/Programma'

class App extends Component {

state = {
  'name': '',
  'token':'',
  'password':'',
  'onScreen':['login']
}

setName = (e)=>{
  this.setState({name:e.target.value})
}

setPassword = (e)=>{
  this.setState({password:e.target.value})
}

setToken = (e) =>{
  e.preventDefault();
  fetch('https://api.ellipsis-earth.com/v2/account/login',{method:'POST',  headers: {'Content-Type': 'application/json'}, body:JSON.stringify({'username':'bruiloft', 'password':this.state.password})})
  .then(res => {
    return([res.json(),res.status])
  })
  .then((data) => {
    if(data[1] === 400){
      alert("Verkeerd wachtwoord, in de verstuurde mail staat het juiste wachtwoord. Letop dat je geen spaties in je wachtwoord hebt. Houd ook rekening met hoofdletters.")
    }
    else{
    this.setState({token: 'Bearer ' + data[0].token, onScreen:['programma']})
  }
    console.log(this.state)
  })
  .catch(console.log)}



  render(){

    let programma;
    if(this.state.onScreen.includes('programma')){
      programma = <Programma/>
    }
    let login;
    if(this.state.onScreen.includes('login')){
      login = <Login name={this.state.name} setName={this.setName} token = {this.state.token} setToken = {this.setToken} password = {this.state.password} setPassword = {this.setPassword} />

    }

  return (
    <div className="App">
    {login}
    {programma}
    </div>
  );
}
}
export default App;
